<?php /** * Created by SSDMAThemes. * User: Dmitry Nizovsky * Date: 13.01.15 * Time: 15:54 */ ?><?php get_header(); $metadata = wp_get_attachment_metadata(); ?><div class="container"><section id="primary" class="content-area image-attachment"><div id="content" class="site-content" role="main"><?php while ( have_posts() ) : the_post(); ?><article id="post-<?php the_ID(); ?>" <?php post_class(); ?>><header class="entry-header"><h1 class="entry-title"><?php the_title(); ?></h1><div class="entry-meta"><span class="entry-date"><time class="entry-date" datetime="<?php echo esc_attr( get_the_date( 'c' ) ); ?>"><?php echo esc_html( get_the_date() ); ?></time></span><?php if($metadata): ?><span class="full-size-link"><a href="<?php echo esc_url( wp_get_attachment_url() ); ?>"><?php echo $metadata['width']; ?> &times; <?php echo $metadata['height']; ?></a></span><?php endif; ?><span class="parent-post-link"><a href="<?php echo esc_url( get_permalink( $post->post_parent ) ); ?>" rel="gallery"><?php echo get_the_title( $post->post_parent ); ?></a></span></div></header><hr/><div class="entry-content"><div class="entry-attachment"><div class="attachment text-center"><img src="<?php echo esc_url( wp_get_attachment_url() ); ?>" alt="<?php the_title(); ?>"/></div><hr/><?php if ( has_excerpt() ) : ?><div class="entry-caption"><?php the_excerpt(); ?></div><?php endif; ?></div><?php the_content(); ?><?php wp_link_pages( array( 'before' => '<div class="page-links"><span class="page-links-title">' . __( 'Pages:', 'ssdma' ) . '</span>', 'after' => '</div>', 'link_before' => '<span>', 'link_after' => '</span>', ) ); ?></div></article><nav><ul class="pager"><li><?php previous_image_link( false, __( 'Previous Image', 'ssdma' )); ?></li><li><?php next_image_link( false, __( 'Next Image', 'ssdma' )); ?></li></ul></nav><?php endwhile; ?></div></section></div><?php get_footer(); ?>